import "./Card.css";
import more from "./more.png";
import { Link } from "react-router-dom";

const Card = (props) => {
  const [likeCount, setLikeCount] = useState(0);
  const [upvoteCount, setUpvoteCount] = useState(0);
  const [downvoteCount, setDownvoteCount] = useState(0);

  const handleLike = () => {
    setLikeCount((count) => count + 1);
  };

  const handleUpvote = () => {
    setUpvoteCount((count) => count + 1);
  };

  const handleDownvote = () => {
    setDownvoteCount((count) => count + 1);
  };

  return (
    <div className="Card">
      <Link to={"edit/" + props.id}>
        <img className="moreButton" alt="edit button" src={more} />
      </Link>
      <h2 className="title">{props.title}</h2>
      <h3 className="author">{"by " + props.author}</h3>
      <p className="description">{props.description}</p>
      <div>
        <button className="voteButton" onClick={handleUpvote}>
          Upvote üëçüèª: {upvoteCount}
        </button>
        <button className="voteButton" onClick={handleDownvote}>
          Downvote üëéüèª: {downvoteCount}
        </button>
      </div>
    </div>
  );
};

export default Card;
