import React from "react";
import { useAuth } from "../config/firebase"; // Import the useAuth hook

const UserProfile = () => {
  const user = useAuth(); // Get the user object using the useAuth hook

  // Check if the user object exists before accessing its properties
  if (!user) {
    return <div>Loading...</div>; // Return a loading indicator while the authentication state is being resolved
  }

  return (
    <div>
      <h1>User Profile</h1>
      <div>
        <h2>User ID:</h2>
        <p>{user.uid}</p>
      </div>
      <div>
        <h2>User Email:</h2>
        <p>{user.email}</p>
      </div>
      <div>
        <h2>Account Created Date:</h2>
        <p>{user.metadata.creationTime}</p>
      </div>
    </div>
  );
};

export default UserProfile;
