import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import firebase from "firebase/app"; // Import Firebase
import { db } from "../config/firebase"; // Import db directly from firebase.js

const CreatePost = () => {
  const { id } = useParams();
  const [post, setPost] = useState({
    id: null,
    title: "",
    author: "CruzIsaiah", // Default author value
    description: "",
  });
  const [submitting, setSubmitting] = useState(false); // State to track form submission

  // useEffect hook to log db object after component mounts
  useEffect(() => {
    console.log(db);
  }, []);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setPost((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setSubmitting(true); // Start form submission
    // Add post to Firestore
    db.collection("posts")
      .add({
        title: post.title,
        body: post.description, // Assuming the 'description' field in the form corresponds to the 'body' field in Firestore
        authorId: post.author,
        upvotes: 0, // Initialize upvotes to 0
        datePosted: firebase.firestore.Timestamp.now(), // Add current timestamp
      })
      .then((docRef) => {
        console.log("Post added with ID: ", docRef.id);
        // Reset the form after successful submission
        setPost({
          id: null,
          title: "",
          author: "CruzIsaiah", // Reset author to default value
          description: "",
        });
        setSubmitting(false); // Complete form submission
        // Optionally, display success message to the user
      })
      .catch((error) => {
        console.error("Error adding post: ", error);
        setSubmitting(false); // Complete form submission
        // Optionally, display error message to the user
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title</label> <br />
        <input
          type="text"
          id="title"
          name="title"
          value={post.title}
          onChange={handleChange}
          required
        />
        <br />
        <br />
        <label htmlFor="description">Description</label> <br />
        <textarea
          rows="5"
          id="description"
          name="description"
          value={post.description}
          onChange={handleChange}
          required
        ></textarea>
        <br />
        <button type="submit" disabled={submitting}>
          {submitting ? "Submitting..." : "Submit"}
        </button>
      </form>
    </div>
  );
};

export default CreatePost;
