import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../config/firebase"; // Import db directly from firebase.js

const CreatePost = () => {
  const { id } = useParams();
  const [post, setPost] = useState({
    id: null,
    title: "",
    author: "CruzIsaiah", // Set author to a default value or fetch it from user authentication
    description: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setPost((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Add post to Firestore
    db.collection("posts")
      .add({
        title: post.title,
        author: post.author,
        description: post.description,
        datePosted: db.FieldValue.serverTimestamp(), // Access FieldValue directly from db
        upvotes: 0, // Initialize upvotes
      })
      .then((docRef) => {
        console.log("Post added with ID: ", docRef.id);
        // Reset the form after successful submission
        setPost({
          id: null,
          title: "",
          author: "CruzIsaiah", // Reset author to default value
          description: "",
        });
      })
      .catch((error) => {
        console.error("Error adding post: ", error);
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title</label> <br />
        <input
          type="text"
          id="title"
          name="title"
          value={post.title}
          onChange={handleChange}
        />
        <br />
        <br />
        <label htmlFor="description">Description</label> <br />
        <textarea
          rows="5"
          id="description"
          name="description"
          value={post.description}
          onChange={handleChange}
        ></textarea>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default CreatePost;
