import React, { useEffect, useState } from "react";
import { useAuth, db } from "../config/firebase"; // Import the useAuth hook and db instance

const UserProfile = () => {
  const user = useAuth(); // Get the user object using the useAuth hook
  const [username, setUsername] = useState(""); // State to store the username

  useEffect(() => {
    // Function to fetch the username from Firestore based on the user's email
    const fetchUsername = async () => {
      try {
        // Query the users collection for the document with the user's email
        const userDoc = await db.collection("users").doc(user.email).get();
        if (userDoc.exists) {
          // If the document exists, retrieve the username
          const userData = userDoc.data();
          setUsername(userData.username);
        } else {
          console.error("User document not found");
        }
      } catch (error) {
        console.error("Error fetching username:", error);
      }
    };

    // Call fetchUsername function only if the user object exists
    if (user) {
      fetchUsername();
    }
  }, [user]); // Run this effect whenever the user object changes

  // Check if the user object exists before accessing its properties
  if (!user) {
    return <div>Loading...</div>; // Return a loading indicator while the authentication state is being resolved
  }

  return (
    <div>
      <h1>User Profile</h1>
      <div>
        <h2>User ID:</h2>
        <p>{user.username}</p>
      </div>
      <div>
        <h2>User Email:</h2>
        <p>{user.email}</p>
      </div>
      <div>
        <h2>Username:</h2>
        <p>{username}</p>
      </div>
      <div>
        <h2>Account Created Date:</h2>
        <p>{user.metadata.creationTime}</p>
      </div>
    </div>
  );
};

export default UserProfile;
