import React, { useState } from "react";
import "./Card.css";
import more from "./more.png";
import { Link } from "react-router-dom";
import Comment from "../components/Comment";

const Card = (props) => {
  const [upvoteCount, setUpvoteCount] = useState(0);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState(""); // State to hold the new comment text

  const handleUpvote = () => {
    setUpvoteCount((count) => count + 1);
  };

  // Function to add a new comment
  const addComment = () => {
    if (newComment.trim() !== "") { // Check if the comment is not empty
      setComments([...comments, { author: "Anonymous", content: newComment }]);
      setNewComment(""); // Clear the input field after adding the comment
    }
  };

  return (
    <div className="Card">
      <Link to={"edit/" + props.id}>
        <img className="moreButton" alt="edit button" src={more} />
      </Link>
      <h2 className="title">{props.title}</h2>
      <h3 className="author">{"by " + props.author}</h3>
      <p className="description">{props.description}</p>
      <div>
        <button className="voteButton" onClick={handleUpvote}>
          Upvote üëçüèª: {upvoteCount}
        </button>
      </div>
      {/* Comment section */}
      <div className="comments-container">
        <h3>Comments</h3>
        {/* Render existing comments */}
        {comments.map((comment, index) => (
          <Comment
            key={index}
            author={comment.author}
            content={comment.content}
          />
        ))}
        {/* Add new comment form */}
        <form
          onSubmit={(e) => {
            e.preventDefault(); // Prevent default form submission behavior
            addComment(); // Call the addComment function to add the new comment
          }}
        >
          <textarea
            className="comment-input"
            placeholder="Your comment"
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)} // Update newComment state as the user types
          ></textarea>
          <button type="submit">Add Comment</button>
        </form>
      </div>
    </div>
  );
};

export default Card;
