import React, { useEffect, useState } from "react";
import { useAuth, db } from "../config/firebase";

const UserProfile = () => {
  const user = useAuth();
  const [username, setUsername] = useState("");
  const [newUsername, setNewUsername] = useState("");
  const [showModal, setShowModal] = useState(false); // State variable to control modal visibility

  useEffect(() => {
    const extractUsername = () => {
      if (user) {
        const email = user.email;
        const atIndex = email.indexOf("@");
        const extractedUsername = atIndex !== -1 ? email.substring(0, atIndex) : email;
        setUsername(extractedUsername);
      }
    };

    extractUsername();
  }, [user]);

  const handleEditUsername = () => {
    setShowModal(true); // Show the modal when Edit Username button is clicked
  };

  const handleSaveUsername = async () => {
    try {
      const userRef = doc(db, "users", user.uid); // Reference to the user document
      await updateDoc(userRef, { username: newUsername }); // Update the username in the document
      setUsername(newUsername); // Update the displayed username
      setShowModal(false); // Close the modal after saving
    } catch (error) {
      console.error("Error updating username:", error);
    }
  };


  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>User Profile</h1>
      <div>
        <h2>User ID:</h2>
        <p>{user.uid}</p>
      </div>
      <div>
        <h2>User Email:</h2>
        <p>{user.email}</p>
      </div>
      <div>
        <h2>Username:</h2>
        <p>{username}</p>
        <button onClick={handleEditUsername}>Edit Username</button>
      </div>
      <div>
        <h2>Account Created Date:</h2>
        <p>{user.metadata.creationTime}</p>
      </div>

      {/* Modal for editing username */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Edit Username</h2>
            <input
              type="text"
              value={newUsername}
              onChange={(e) => setNewUsername(e.target.value)}
            />
            <button onClick={handleSaveUsername}>Save</button>
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
