import React, { useState } from "react";
import { db } from "../config/firebase";
import { collection, addDoc, serverTimestamp } from "firebase/firestore";
import { useAuth } from "../config/firebase"; // Import useAuth hook
import "./CreatePost.css";

const CreatePost = () => {
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [submitting, setSubmitting] = useState(false);
  const user = useAuth(); // Use the useAuth hook to get the current user

  const handleSubmit = async (event) => {
    event.preventDefault();
    setSubmitting(true);

    try {
      await addDoc(collection(db, "posts"), {
        title,
        body,
        datePosted: serverTimestamp(),
        // Add 'by' followed by the username extracted from the user's email
        author: user ? `by ${extractUsername(user.email)}` : "Anonymous",
      });

      // Reset form fields after successful submission
      setTitle("");
      setBody("");
    } catch (error) {
      console.error("Error adding post:", error);
    } finally {
      setSubmitting(false);
    }
  };

  // Function to extract username from email
  const extractUsername = (email) => {
    const atIndex = email.indexOf("@");
    return atIndex !== -1 ? email.substring(0, atIndex) : email;
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title</label> <br />
        <input
          type="text"
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <br />
        <br />
        <label htmlFor="body">Body</label> <br />
        <textarea
          rows="5"
          id="body"
          value={body}
          onChange={(e) => setBody(e.target.value)}
          required
        ></textarea>
        <br />
        <button type="submit" disabled={submitting}>
          {submitting ? "Submitting..." : "Submit"}
        </button>
      </form>
    </div>
  );
};

export default CreatePost;
