import React, { useEffect, useState } from "react";
import { useAuth, db } from "../config/firebase";

const UserProfile = () => {
  const user = useAuth();
  const [username, setUsername] = useState("");
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    const fetchUsername = async () => {
      try {
        if (user) {
          // Fetch username from Firestore based on user's email
          const userDoc = await db.collection("users").doc(user.email).get();
          if (userDoc.exists) {
            const userData = userDoc.data();
            setUsername(userData.username);
          } else {
            console.error("User document not found");
          }
        }
      } catch (error) {
        console.error("Error fetching username:", error);
      } finally {
        setLoading(false); // Set loading state to false after fetching data
      }
    };

    fetchUsername();
  }, [user]); // Make sure to include user in the dependency array

  if (!user || loading) { // Check if user or loading state is true
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>User Profile</h1>
      <div>
        <h2>User ID:</h2>
        <p>{user.uid}</p>
      </div>
      <div>
        <h2>User Email:</h2>
        <p>{user.email}</p>
      </div>
      <div>
        <h2>Username:</h2>
        <p>{username}</p>
      </div>
      <div>
        <h2>Account Created Date:</h2>
        <p>{user.metadata.creationTime}</p>
      </div>
    </div>
  );
};

export default UserProfile;
