import React from "react";
import "./UserProfile.css";
import { useAuth } from "../config/firebase"; // Import useAuth hook to access user information

const UserProfile = () => {
  const user = useAuth(); // Get the currently logged-in user

  return (
    <div className="UserProfile">
      <h1>User Profile</h1>
      <div>
        <h2>User ID:</h2>
        <p>{user.uid}</p> {/* Display user ID */}
      </div>
      <div>
        <h2>Email:</h2>
        <p>{user.email}</p> {/* Display user email */}
      </div>
      <div>
        <h2>Sign Up Timestamp:</h2>
        <p>{user.metadata.creationTime}</p> {/* Display sign-up timestamp */}
      </div>
    </div>
  );
};

export default UserProfile;
