import React, { useEffect, useState } from "react";
import { useAuth, fetchUsername } from "../config/firebase"; // Updated import

const UserProfile = () => {
  const user = useAuth();
  const [username, setUsername] = useState("");

  useEffect(() => {
    const fetchUserData = async () => {
      if (user) {
        const fetchedUsername = await fetchUsername(user.email); // Call fetchUsername function
        setUsername(fetchedUsername); // Update the username state
      }
    };

    fetchUserData();
  }, [user]);

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>User Profile</h1>
      <div>
        <h2>User ID:</h2>
        <p>{user.uid}</p>
      </div>
      <div>
        <h2>User Email:</h2>
        <p>{user.email}</p>
      </div>
      <div>
        <h2>Username:</h2>
        <p>{user.username}</p>
      </div>
      <div>
        <h2>Account Created Date:</h2>
        <p>{user.metadata.creationTime}</p>
      </div>
    </div>
  );
};

export default UserProfile;
