import React from "react";
import { useRoutes, useLocation } from "react-router-dom";
import ReadPosts from "./pages/ReadPosts";
import CreatePost from "./pages/CreatePost";
import EditPost from "./pages/EditPost";
import UserProfile from "./pages/UserProfile";
import Login from "./pages/Login";
import { Link } from "react-router-dom";
import { auth } from "./config/firebase"; // Import the auth module from your Firebase configuration
import "./App.css";

const App = () => {
  const location = useLocation();
  const hideHeader = location.pathname === "/login";

  const handleLogout = () => {
    // Use the signOut function from the auth module to log the user out
    auth.signOut().then(() => {
      // Redirect or perform any necessary actions after logout
    }).catch((error) => {
      console.error("Error logging out:", error);
    });
  };

  const posts = [];

  let element = useRoutes([
    {
      path: "/",
      element: <ReadPosts data={posts} />,
    },
    {
      path: "/edit/:id",
      element: <EditPost data={posts} />,
    },
    {
      path: "/new",
      element: <CreatePost />,
    },
    {
      path: "/profile",
      element: <UserProfile />,
    },
    {
      path: "/login",
      element: <Login />,
    },
  ]);

  return (
    <div className="App">
      {!hideHeader && (
        <div className="header">
          <div className="logo">
            <img src="boldredlogo.png" alt="Twisted Lit" />
          </div>
          <Link to="/">
            <button className="headerBtn"> Feed </button>
          </Link>
          <Link to="/new">
            <button className="headerBtn"> Create Post </button>
          </Link>
          <button onClick={handleLogout} className="headerBtn"> Logout </button> {/* Add logout button */}
        </div>
      )}
      {element}
      {!hideHeader && (
        <div className="circleButton">
          <Link to="/profile">
            <img src="profile.png" alt="Profile" />
          </Link>
        </div>
      )}
    </div>
  );
};

export default App;
