import React, { useEffect, useState } from "react";
import { useAuth, db } from "../config/firebase"; 

const UserProfile = () => {
  const user = useAuth(); 
  const [userData, setUserData] = useState(null); 

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        if (user) {
          const userDoc = await db.collection("details").doc(user.uid).get();
          if (userDoc.exists) {
            const userData = userDoc.data();
            setUserData(userData);
          } else {
            console.error("User document not found");
          }
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUserData();
  }, [user]);

  if (!user || !userData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>User Profile</h1>
      <div>
        <h2>User ID:</h2>
        <p>{user.uid}</p>
      </div>
      <div>
        <h2>User Email:</h2>
        <p>{userData.email}</p>
      </div>
      <div>
        <h2>Username:</h2>
        <p>{userData.username}</p>
      </div>
      <div>
        <h2>Account Created Date:</h2>
        <p>{userData.accountCreatedDate}</p>
      </div>
    </div>
  );
};

export default UserProfile;
