import React, { useState } from "react";
import "./Card.css";
import more from "./more.png";
import { Link } from "react-router-dom";
import Comment from "../components/Comment";

const Card = ({ id, title, author, description, date }) => {
  const [upvoteCount, setUpvoteCount] = useState(0);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");

  const handleUpvote = () => {
    setUpvoteCount((count) => count + 1);
  };

  const addComment = () => {
    if (newComment.trim() !== "") {
      setComments([...comments, { author: "Anonymous", content: newComment }]);
      setNewComment("");
    }
  };

  // Convert Firestore Timestamp to JavaScript Date object
  const datePosted = date ? date.toDate().toLocaleString() : "";

  return (
    <div className="Card">
      <Link to={"edit/" + id}>
        <img className="moreButton" alt="edit button" src={more} />
      </Link>
      <h2 className="title">{title}</h2>
      <h3 className="author">{"by " + author}</h3>
      <p className="description">{description}</p>
      <p className="date">Date Posted: {datePosted}</p> {/* Render the date here */}
      <div>
        <button className="voteButton" onClick={handleUpvote}>
          Upvote üëçüèª: {upvoteCount}
        </button>
      </div>
      <div className="comments-container">
        <h3>Comments</h3>
        {comments.map((comment, index) => (
          <Comment
            key={index}
            author={comment.author}
            content={comment.content}
          />
        ))}
        <form
          onSubmit={(e) => {
            e.preventDefault();
            addComment();
          }}
        >
          <textarea
            className="comment-input"
            placeholder="Your comment"
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
          ></textarea>
          <button type="submit">Add Comment</button>
        </form>
      </div>
    </div>
  );
};

export default Card;
