import React, { useState, useEffect } from "react"; // Importing React and useState from 'react'
import { useParams } from "react-router-dom";
import "./EditPost.css";

const EditPost = ({ data }) => {
  const { id } = useParams();
  const [post, setPost] = useState({
    id: null,
    title: "",
    author: "",
    description: "", // initializing description as an empty string
  });

  // Set initial state values when the component mounts
  useEffect(() => {
    if (data) {
      setPost({
        id: data.id,
        title: data.title,
        author: data.author,
        description: data.description, // Set description from the data
      });
    }
  }, [data]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setPost((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });
  };

  return (
    <div>
      <form>
        {/* Removed the <label> element and added placeholder */}
        <input
          type="text"
          id="title"
          name="title"
          value={post.title}
          onChange={handleChange}
          placeholder="Title"
        />
        <br />
        <br />
        {/* <label htmlFor="author">Author</label>
        <br />
        <input
          type="text"
          id="author"
          name="author"
          value={post.author}
          onChange={handleChange}
        /> */}
        {/* <br /> */}
        {/* <br /> */}
        <label htmlFor="description">Description</label>
        <br />
        <textarea
          rows="20"
          cols="50"
          id="description"
          value={post.description}
          onChange={handleChange}
        ></textarea>
        <br />
        <input type="submit" value="Submit" />
        <button className="deleteButton">Delete</button>
      </form>
    </div>
  );
};

export default EditPost;
